import axios from 'axios';

import React from 'react';
import ReactDOM from 'react-dom/client'; // שים לב לייבוא החדש
import App from './App';

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<App />);




const apiUrl = "http://localhost:5285"

export default {
  getTasks: async () => {
    console.log('getTasks')
    const result = await axios.get(`${apiUrl}/items`)
    return result.data
  },

  addTask: async (name) => {
    console.log('addTask', name)
    const result = await axios.post(`${apiUrl}/items`, { name })
    return result.data
  },

  setCompleted: async (id, isComplete) => {
    console.log('setCompleted', { id, isComplete })
    const result = await axios.put(`${apiUrl}/items/${id}`, { isComplete: Boolean(isComplete) })
    return result.data
  },

  deleteTask: async (id) => {
    console.log('deleteTask', id)
    const result = await axios.delete(`${apiUrl}/items/${id}`)
    return result.data
  }
};
